void sorter(std::vector<int> list) {
    bool sorted = false;
    std::size_t sz = list.size();
    std::size_t cnt = list.size();

    while (cnt--) {
        for (std::size_t i = 0; i < sz - 1; ++i) {
            if (list[i] > list[i + 1]) {
                int tmp = list[i + 1];
                list[i + 1] = list[i];
                list[i] = tmp;
            }
        }
    }
    for (std::size_t i = 0; i < list.size(); ++i) {std::cout << list.at(i) << ", ";}
    std::cout << "\n";
}

